import { existsSync, promises } from 'node:fs';
import { d as defineCommand, c as consola } from '../shared/nuxi.DSPrE7vg.mjs';
import 'node:util';
import 'node:path';
import 'node:process';
import 'node:tty';
import { l as loadKit } from '../shared/nuxi.C7GxbkTn.mjs';
import { u as upperFirst, c as camelCase } from '../shared/nuxi.B21HxevS.mjs';
import { r as resolve, c as cwdArgs, l as logLevelArgs, e as extname, d as dirname } from '../shared/nuxi.BrnifDS2.mjs';
import 'node:url';
import 'node:perf_hooks';
import '../shared/nuxi.D2_bzAv0.mjs';
import './satisfies.mjs';
import '../shared/nuxi.E-ZsRS8r.mjs';
import '../shared/nuxi.BPuj-7ta.mjs';
import '../shared/nuxi.a6bzaP2S.mjs';
import '../shared/nuxi.BLjynjc6.mjs';
import 'node:module';
import '../shared/nuxi.bcvuOb7W.mjs';
import 'node:assert';
import 'node:v8';
import '../shared/nuxi.BdnFVL_h.mjs';
