import { promises } from 'node:fs';
import { c as createApp, e as eventHandler, l as lazyEventHandler, t as toNodeListener } from '../shared/nuxi.CBWIBKrA.mjs';
import { listen } from './index2.mjs';
import { d as defu } from '../shared/nuxi.D2_bzAv0.mjs';
import { d as defineCommand } from '../shared/nuxi.DSPrE7vg.mjs';
import { l as loadKit } from '../shared/nuxi.C7GxbkTn.mjs';
import { c as clearDir } from '../shared/nuxi.DNA8U7Ya.mjs';
import { o as overrideEnv } from '../shared/nuxi.CgS_bFBf.mjs';
import { c as cwdArgs, l as logLevelArgs, a as legacyRootDirArgs, b as dotEnvArgs, r as resolve, j as join } from '../shared/nuxi.BrnifDS2.mjs';
import '../shared/nuxi.bcvuOb7W.mjs';
import 'node:http';
import 'node:https';
import 'node:util';
import 'node:net';
import 'node:os';
import '../shared/nuxi.E-ZsRS8r.mjs';
import 'http';
import 'https';
import 'node:path';
import 'node:process';
import 'node:tty';
import 'node:child_process';
import 'crypto';
import 'node:fs/promises';
import 'node:url';
import 'node:perf_hooks';
import './satisfies.mjs';
import '../shared/nuxi.BPuj-7ta.mjs';
import '../shared/nuxi.a6bzaP2S.mjs';
import '../shared/nuxi.BLjynjc6.mjs';
import 'node:module';
import 'node:assert';
import 'node:v8';
import '../shared/nuxi.BdnFVL_h.mjs';
import 'node:crypto';
import 'node:vm';
import 'assert';
import 'fs';
import 'module';
import 'os';
import 'path';
import 'process';
import 'tty';
import 'url';
import 'util';
import 'v8';
import '../shared/nuxi.wVjcfr_F.mjs';
import '../shared/nuxi.BAWBxh_J.mjs';

const analyze = defineCommand({
  meta: {
    name: "analyze",
    description: "Build nuxt and analyze production bundle (experimental)"
  },
  args: {
    ...cwdArgs,
    ...logLevelArgs,
    ...legacyRootDirArgs,
    ...dotEnvArgs,
    name: {
      type: "string",
      description: "Name of the analysis",
      default: "default"
    },
    serve: {
      type: "boolean",
      description: "Serve the analysis results",
      default: true
    }
  },
  async run(ctx) {
    overrideEnv("production");
    const cwd = resolve(ctx.args.cwd || ctx.args.rootDir);
    const name = ctx.args.name || "default";
    const slug = name.trim().replace(/[^\w-]/g, "_");
    const startTime = Date.now();
    const { loadNuxt, buildNuxt } = await loadKit(cwd);
    const nuxt = await loadNuxt({
      cwd,
      dotenv: {
        cwd,
        fileName: ctx.args.dotenv
      },
      overrides: defu(ctx.data?.overrides, {
        build: {
          analyze: {
            enabled: true
          }
        },
        vite: {
          build: {
            rollupOptions: {
              output: {
                chunkFileNames: "_nuxt/[name].js",
                entryFileNames: "_nuxt/[name].js"
              }
            }
          }
        },
        logLevel: ctx.args.logLevel
      })
    });
    const analyzeDir = nuxt.options.analyzeDir;
    const buildDir = nuxt.options.buildDir;
    const outDir = nuxt.options.nitro.output?.dir || join(nuxt.options.rootDir, ".output");
    nuxt.options.build.analyze = defu(nuxt.options.build.analyze, {
      filename: join(analyzeDir, "client.html")
    });
    await clearDir(analyzeDir);
    await buildNuxt(nuxt);
    const endTime = Date.now();
    const meta = {
      name,
      slug,
      startTime,
      endTime,
      analyzeDir,
      buildDir,
      outDir
    };
    await nuxt.callHook("build:analyze:done", meta);
    await promises.writeFile(
      join(analyzeDir, "meta.json"),
      JSON.stringify(meta, null, 2),
      "utf-8"
    );
    console.info("Analyze results are available at: `" + analyzeDir + "`");
    console.warn(
      "Do not deploy analyze results! Use `nuxi build` before deploying."
    );
    if (ctx.args.serve !== false && !process.env.CI) {
      const app = createApp();
      const serveFile = (filePath) => lazyEventHandler(async () => {
        const contents = await promises.readFile(filePath, "utf-8");
        return eventHandler((event) => {
          event.node.res.end(contents);
        });
      });
      console.info("Starting stats server...");
      app.use("/client", serveFile(join(analyzeDir, "client.html")));
      app.use("/nitro", serveFile(join(analyzeDir, "nitro.html")));
      app.use(
        eventHandler(
          () => `<!DOCTYPE html>
        <html lang="en">
        <head>
        <meta charset="utf-8">
        <title>Nuxt Bundle Stats (experimental)</title>
        </head>
          <h1>Nuxt Bundle Stats (experimental)</h1>
          <ul>
            <li>
              <a href="/nitro">Nitro server bundle stats</a>
            </li>
            <li>
              <a href="/client">Client bundle stats</a>
            </li>
          </ul>
        </html>
      `
        )
      );
      await listen(toNodeListener(app));
    }
  }
});

export { analyze as default };
