import { d as defineCommand, c as consola } from '../shared/nuxi.DSPrE7vg.mjs';
import 'node:util';
import 'node:path';
import 'node:process';
import 'node:tty';
import { l as loadKit } from '../shared/nuxi.C7GxbkTn.mjs';
import { a as clearBuildDir } from '../shared/nuxi.DNA8U7Ya.mjs';
import { o as overrideEnv } from '../shared/nuxi.CgS_bFBf.mjs';
import { s as showVersions } from '../shared/nuxi.D3y0V8na.mjs';
import { c as cwdArgs, l as logLevelArgs, b as dotEnvArgs, f as envNameArgs, a as legacyRootDirArgs, r as resolve, g as relative } from '../shared/nuxi.BrnifDS2.mjs';
import 'node:url';
import 'node:fs';
import 'node:perf_hooks';
import '../shared/nuxi.D2_bzAv0.mjs';
import './satisfies.mjs';
import '../shared/nuxi.E-ZsRS8r.mjs';
import '../shared/nuxi.BPuj-7ta.mjs';
import '../shared/nuxi.a6bzaP2S.mjs';
import '../shared/nuxi.BLjynjc6.mjs';
import 'node:module';
import '../shared/nuxi.bcvuOb7W.mjs';
import 'node:assert';
import 'node:v8';
import '../shared/nuxi.BdnFVL_h.mjs';
import 'node:os';
import 'node:crypto';
import 'node:vm';
import 'assert';
import 'fs';
import 'module';
import 'os';
import 'path';
import 'process';
import 'tty';
import 'url';
import 'util';
import 'v8';
import 'node:fs/promises';
import '../shared/nuxi.wVjcfr_F.mjs';
import '../shared/nuxi.BAWBxh_J.mjs';
import '../shared/nuxi.C0R9QFgG.mjs';
import '../shared/nuxi.C4yw6GLN.mjs';

const buildCommand = defineCommand({
  meta: {
    name: "build",
    description: "Build Nuxt for production deployment"
  },
  args: {
    ...cwdArgs,
    ...logLevelArgs,
    prerender: {
      type: "boolean",
      description: "Build Nuxt and prerender static routes"
    },
    preset: {
      type: "string",
      description: "Nitro server preset"
    },
    ...dotEnvArgs,
    ...envNameArgs,
    ...legacyRootDirArgs
  },
  async run(ctx) {
    overrideEnv("production");
    const cwd = resolve(ctx.args.cwd || ctx.args.rootDir);
    showVersions(cwd);
    const kit = await loadKit(cwd);
    const nitroPreset = ctx.args.prerender ? "static" : ctx.args.preset || process.env.NITRO_PRESET || process.env.SERVER_PRESET;
    if (nitroPreset) {
      if (ctx.args.prerender && ctx.args.preset) {
        consola.warn(`\`--prerender\` is set. Ignoring \`--preset ${ctx.args.preset}\``);
      }
      consola.info(`Using Nitro server preset: \`${nitroPreset}\``);
    }
    const nuxt = await kit.loadNuxt({
      cwd,
      dotenv: {
        cwd,
        fileName: ctx.args.dotenv
      },
      envName: ctx.args.envName,
      // c12 will fall back to NODE_ENV
      overrides: {
        logLevel: ctx.args.logLevel,
        // TODO: remove in 3.8
        _generate: ctx.args.prerender,
        ...ctx.args.prerender ? { nitro: { static: true } } : { nitro: { preset: nitroPreset } },
        ...ctx.data?.overrides
      }
    });
    let nitro;
    try {
      nitro = kit.useNitro?.();
    } catch {
    }
    await clearBuildDir(nuxt.options.buildDir);
    await kit.writeTypes(nuxt);
    nuxt.hook("build:error", (err) => {
      consola.error("Nuxt Build Error:", err);
      process.exit(1);
    });
    await kit.buildNuxt(nuxt);
    if (ctx.args.prerender) {
      if (!nuxt.options.ssr) {
        consola.warn(
          "HTML content not prerendered because `ssr: false` was set. You can read more in `https://nuxt.com/docs/getting-started/deployment#static-hosting`."
        );
      }
      const dir = nitro?.options.output.publicDir;
      const publicDir = dir ? relative(process.cwd(), dir) : ".output/public";
      consola.success(
        `You can now deploy \`${publicDir}\` to any static hosting!`
      );
    }
  }
});

export { buildCommand as default };
